import React, { useEffect } from 'react'
import {HiPencilAlt} from 'react-icons/hi'
import {FaRedo} from 'react-icons/fa'
import { useHistory } from 'react-router-dom'

const CreateBlog = ()=>{
    const history = useHistory()

    const [data,setData] = useState({
        title:'',msg:''
    })
    const handleInputs = (e)=>{
        let name = e.target.name;
        let value = e.target.value;
        setUser({...user, [name]:value})
    }
    const callAdminBlog=async()=>{
        try{
            const res = await fetch('/createBlog',{
                method:'GET',
                headers:{
                    Accept:'application/json',
                    "Content-Type":"application/json"
                },
                credentials:"include"
            });
        const data = await res.json();
        if(!res.status == 200){
            const error = new Error(res.error)
            throw error
        }
        else{
            history.push('/CreateBlog');
        }
        }
        catch(err){
            console.log(err);
            history.push('/login');
        }
    }

    useEffect(()=>{
        callAdminBlog();
    },[]);


    return(
        <>
            <div className='row contain1 min-vh-100'>
                <div className='text-center mt-5'>
                    <strong className='text-center'>Write a Blog</strong>
                    <form className='text-center'>
                    <label for='title'>Title</label>
                    <input type='text' name='title'></input>
                    <br/><br/>
                    <textarea name="blogbody" rows="4" className='w-75'/>
                    <br></br>
                    <div className='text-center'>
                    <button className="btn btn-primary text-center">Post</button>
                    </div>
                    </form>
                </div>
            </div>
            
        </>
    )
}

export default CreateBlog
